mapper.select:
select count(id) > 0
from tbl_post
where post_status ='active' and id=#{postId}

mapper.update:
update tbl_post
set post_title = #{postTitle},updated_datetime = now()
where id = #{id}

update tbl_post
set post_status = 'inactive'
where id =#{id}

update tbl_post
set post_read_count = post_read_count + 1
where id = #{postId}
